import YouTube from 'react-youtube';
import React from 'react';



function MovieTrailer(props) {
  <div>
        <main>
          {props.movie ? (
            <div
              className="viewtrailer"
              style={{
                backgroundImage: `url("${props.IMAGE_PATH}${props.movie.backdrop_path}")`,
              }}
            >
              {props.playing ? (
                <>
                  <YouTube
                    videoId={props.trailer.key}
                    className="reproductor container"
                    containerClassName={"youtube-container amru"}
                    opts={{
                      width: "100%",
                      height: "100%",
                      playerVars: {
                        autoplay: 1,
                        controls: 1,
                        cc_load_policy: 0,
                        fs: 0,
                        iv_load_policy: 0,
                        modestbranding: 0,
                        rel: 0,
                        showinfo: 0,
                      },
                    }}
                  />
                  <button onClick={() => props.functionsetPlaying(false)} className="boton">
                    Close
                  </button>
                </>
              ) : (
                <div className="container">
                  <div className="">
                    {props.trailer ? (
                      <button
                        className="boton"
                        onClick={() => props.functionsetPlaying(true)}
                        type="button"
                      >
                        Play Trailer
                      </button>
                    ) : (
                      "Sorry, no trailer available"
                    )}
                    <h1 className="text-white">{props.movie.title}</h1>
                    <p className="text-white">{props.movie.overview}</p>
                  </div>
                </div>
              )}
            </div>
          ) : null}
        </main>
      </div>
    
  }
  export default MovieTrailer;
