import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
function SearchBar( props ) {
    
  
    return (
      <Form className="container mb-4" onSubmit={props.searchMovies}>
        <Form.Control type="text" placeholder="search" onChange={(e) => props.functionSetSearchBar(e.target.value)} />
        {/* <input
          type="text"
          placeholder="search"
          onChange={(e) => props.functionSetSearchBar(e.target.value)}
        /> */}
        <Button variant="primary">Buscar</Button>
      </Form>
    );
  }
export default SearchBar
