import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
function MovieList(props) {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 3,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  return (
    <div className="container mt-3" id="moviesList">
      <div className="row">
        <Carousel
          additionalTransfrom={0}
          arrows
          centerMode
          className=""
          containerClass="container"
          dotListClass=""
          draggable
          focusOnSelect={false}
          itemClass=""
          minimumTouchDrag={80}
          renderArrowsWhenDisabled={false}
          renderButtonGroupOutside={false}
          renderDotsOutside={false}
          responsive={
            responsive
          }
          rewind={true}
          rewindWithAnimation={true}
          rtl={false}
          shouldResetAutoplay
          showDots={false}
          sliderClass=""
          slidesToSlide={1}
          swipeable
        >
          {props.movies
            .filter((m, i) => i > 10)
            .map((movie) => (
              <div
                key={movie.id}
                className="movie"
                onClick={() => props.functionSelectMovie(movie)}
              >
                <img
                  src={`${props.IMAGE_PATH + movie.poster_path}`}
                  alt={movie.title}
                />
                <h4 className="text-center">{movie.title}</h4>
              </div>
            ))}
        </Carousel>
        <Carousel
          additionalTransfrom={0}
          arrows
          centerMode
          className=""
          containerClass="container"
          dotListClass=""
          draggable
          focusOnSelect={false}
          itemClass=""
          minimumTouchDrag={80}
          renderArrowsWhenDisabled={false}
          renderButtonGroupOutside={false}
          renderDotsOutside={false}
          responsive={responsive
            
          }
          rewind={true}
          rewindWithAnimation={true}
          rtl={false}
          shouldResetAutoplay
          showDots={false}
          sliderClass=""
          slidesToSlide={1}
          swipeable
        >
          {props.movies
            .filter((m, i) => i < 10)
            .map((movie) => (
              <div
                key={movie.id}
                className="movie"
                onClick={() => props.functionSelectMovie(movie)}
              >
                <img
                  src={`${props.IMAGE_PATH + movie.poster_path}`}
                  alt={movie.title}
                />
                <h4>{movie.title}</h4>
              </div>
            ))}
        </Carousel>
        
      </div>
    </div>
  );
}
export default MovieList;
